mapper.select:
SELECT * FROM receiptSchedule
WHERE pfRate=#{pfRate}
and deposit=#{deposit}

SELECT pfID
FROM PF
WHERE pfName=#{pfName}

SELECT COUNT(uNo)
FROM roll
WHERE pfID = #{pfID}

SELECT pfEntry
FROM PF
WHERE pfID = #{pfID}

SELECT startDate
FROM PF
WHERE pfID = #{pfID}

mapper.update:
UPDATE PF
SET startFlag = '참여중'
WHERE pfID = #{pfID}

UPDATE PF
SET startDate = SYSDATE
WHERE pfID = #{pfID}

UPDATE PF
SET endDate = #{endDate}
WHERE pfID = #{pfID}

mapper.delete:
DELETE FROM roll
WHERE uNo = #{uNo}
AND pfID = #{pfID}

mapper.update:
UPDATE pf
SET stageBalance = stageBalance + #{uPayment}
WHERE pfID = #{pfID}

UPDATE MyAccount
SET myBalance = myBalance - #{uPayment}
WHERE uNo = #{uNo}

UPDATE roll
SET depositCnt = depositCnt + 1
WHERE uNo = #{uNo}
AND pfID = #{pfID}

UPDATE roll
SET stageAmount = stageAmount + #{uPayment}
WHERE uNo = #{uNo}
AND pfID = #{pfID}

UPDATE roll
SET paymentCheck = 'Y'
WHERE uNo = #{uNo}
AND pfID = #{pfID}

mapper.select:
SELECT stageBalance
FROM pf
WHERE pfID = #{pfID}

SELECT deposit
FROM pf
WHERE pfID = #{pfID}

SELECT paymentOrder
FROM pf
WHERE pfID = #{pfID}

mapper.update:
UPDATE PF
SET stageBalance = stageBalance - deposit
WHERE pfID = #{pfID}

UPDATE MyAccount
SET myBalance = myBalance + #{uTotalReceipts}
WHERE uno = #{uNo}

UPDATE PF
SET paymentOrder = paymentOrder + 1
WHERE pfID = #{pfID}

mapper.select:
SELECT *
FROM MEMBER
WHERE uNo = #{uNo}

SELECT MIN(stageInDate)
FROM roll
WHERE pfID = #{pfID}

mapper.update:
UPDATE roll
SET pfMaster = 'M'
WHERE stageInDate = #{stageInDate}

mapper.select:
SELECT pfMaster
FROM roll
WHERE uNo = #{uNo}
AND pfID = #{pfID}

SELECT *
FROM PF
WHERE pfID = #{pfID}
